import Wrapper from "./compontents/Wrapper";
const btnValues = [
  ["C","+-","%","/"],
  [7,8,9 ,"x"],
  [4,5,6 ,"-"],
  [1,2,3 ,"+"],
  [0, ".", "="],
];

function App() {
  return (
    <CalcProvider>
      <Wrapper>
        <Screen />
        <ButtonBox>
          {btnValues.flat().map((btn , i) => (
            <MyButton 
             value={btn}
             key={i}
            />  
          ))}
        </ButtonBox>  

      </Wrapper>
    </CalcProvider>
  );
}

export default App;
